import React, { useState } from "react";

const BALLS_PER_OVER = 6;
const OVERS_PER_ROW = 5;

const CricketPrediction = () => {
    const initialOvers = Array.from({ length: 20 }, () => ({
        balls: [],
        isFrozen: false,
    }));

    const [overs, setOvers] = useState(initialOvers);

    const runOptions = ["0", "1", "2", "3", "4", "5", "6", "Wide", "No Ball", "Wicket"];

    const handlePrediction = (overIndex, run) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            if (currentOver.isFrozen) return newOvers;

            const balls = [...currentOver.balls];
            const legalBalls = balls.filter((ball) => ball !== "Wide" && ball !== "No Ball").length;

            if (legalBalls >= BALLS_PER_OVER && run !== "Wide" && run !== "No Ball") return newOvers;

            balls.push(run);
            currentOver.balls = balls;
            newOvers[overIndex] = currentOver;

            return newOvers;
        });
    };

    const deleteLastEntry = (overIndex) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            if (currentOver.isFrozen) return newOvers;

            const balls = [...currentOver.balls];
            if (balls.length > 0) balls.pop();

            currentOver.balls = balls;
            newOvers[overIndex] = currentOver;

            return newOvers;
        });
    };

    const playOver = (overIndex) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            const legalBalls = currentOver.balls.filter((ball) => ball !== "Wide" && ball !== "No Ball").length;
            if (legalBalls < BALLS_PER_OVER) {
                alert("You must enter all 6 legitimate balls before confirming!");
                return newOvers;
            }

            currentOver.isFrozen = true;
            newOvers[overIndex] = currentOver;
            return newOvers;
        });
    };

    const undoOver = (overIndex) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            newOvers[overIndex] = { ...newOvers[overIndex], isFrozen: false };
            return newOvers;
        });
    };

    const getBallStyle = (ball) => {
        if (ball === "Wicket") return { backgroundColor: "red", color: "white" };
        if (ball === "No Ball") return { backgroundColor: "blue", color: "white" };
        if (ball === "Wide") return { backgroundColor: "green", color: "white" };
        if (ball === "4" || ball === "6") return { color: "orange" };
        return { color: "black" };
    };

    return (
        <div style={styles.container}>
            <h1>Cricket Prediction</h1>

            <div>
                {Array.from({ length: Math.ceil(overs.length / OVERS_PER_ROW) }, (_, rowIndex) => (
                    <div key={rowIndex} style={styles.row}>
                        {overs.slice(rowIndex * OVERS_PER_ROW, (rowIndex + 1) * OVERS_PER_ROW).map((over, index) => {
                            const globalIndex = rowIndex * OVERS_PER_ROW + index;
                            return (
                                <div key={globalIndex} style={{ ...styles.overBox, backgroundColor: over.isFrozen ? "#d4edda" : "#f9f9f9" }}>
                                    <h4>Over {globalIndex + 1}</h4>

                                    <div style={styles.ballDisplay}>
                                        {Array.from({ length: BALLS_PER_OVER }, (_, ballIndex) => (
                                            <div key={ballIndex} style={styles.ballContainer}>
                                                <span style={styles.ballNumber}>{ballIndex + 1}st</span>
                                                <span
                                                    style={{
                                                        ...styles.ballCircle,
                                                        ...getBallStyle(over.balls[ballIndex] || ""),
                                                    }}
                                                >
                                                    {over.balls[ballIndex]
                                                        ? over.balls[ballIndex] === "Wicket"
                                                            ? "WK"
                                                            : over.balls[ballIndex] === "No Ball"
                                                            ? "NB"
                                                            : over.balls[ballIndex] === "Wide"
                                                            ? "WD"
                                                            : over.balls[ballIndex]
                                                        : "-"}
                                                </span>
                                            </div>
                                        ))}
                                    </div>

                                    <div style={styles.buttonsContainer}>
                                        {runOptions.map((run, i) => (
                                            <button
                                                key={i}
                                                onClick={() => handlePrediction(globalIndex, run)}
                                                style={{
                                                    ...styles.runButton,
                                                    fontSize: isNaN(run) ? "10px" : "12px",
                                                    opacity: over.isFrozen ? 0.5 : 1,
                                                    cursor: over.isFrozen ? "not-allowed" : "pointer",
                                                }}
                                                disabled={over.isFrozen}
                                            >
                                                {run}
                                            </button>
                                        ))}
                                    </div>

                                    {!over.isFrozen ? (
                                        <>
                                            <button onClick={() => deleteLastEntry(globalIndex)} style={styles.deleteButton}>
                                                Delete Last Entry
                                            </button>
                                            <button onClick={() => playOver(globalIndex)} style={styles.freezeButton}>
                                                Play
                                            </button>
                                        </>
                                    ) : (
                                        <>
                                            <p style={styles.frozenText}>Over Confirmed ✅</p>
                                            <button onClick={() => undoOver(globalIndex)} style={styles.undoButton}>
                                                Undo
                                            </button>
                                        </>
                                    )}
                                </div>
                            );
                        })}
                    </div>
                ))}
            </div>
        </div>
    );
};

const styles = {
    container: {
        padding: "10px",
        textAlign: "center",
        fontFamily: "Arial, sans-serif",
    },
    row: {
        display: "flex",
        justifyContent: "center",
        gap: "10px",
        flexWrap: "wrap",
        marginBottom: "10px",
    },
    overBox: {
        border: "1px solid #ccc",
        padding: "8px",
        borderRadius: "5px",
        textAlign: "center",
        width: "180px",
    },
    ballDisplay: {
        display: "flex",
        gap: "5px",
        justifyContent: "center",
    },
    ballContainer: {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
    },
    ballNumber: {
        fontSize: "8px",
        marginBottom: "2px",
    },
    ballCircle: {
        width: "30px",
        height: "30px",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        borderRadius: "50%",
        fontSize: "14px",
        fontWeight: "bold",
        border: "1px solid black",
    },
    buttonsContainer: {
        display: "flex",
        flexWrap: "wrap",
        justifyContent: "center",
        gap: "5px",
    },
    runButton: {
        padding: "8px",
        fontSize: "12px",
        backgroundColor: "#007bff",
        color: "white",
        border: "none",
        borderRadius: "3px",
        minWidth: "40px",
    },
    deleteButton: {
        marginTop: "5px",
        padding: "5px",
        fontSize: "12px",
        backgroundColor: "#dc3545",
        color: "white",
        border: "none",
        borderRadius: "3px",
        width: "100%",
    },
    freezeButton: {
        marginTop: "5px",
        padding: "5px",
        fontSize: "12px",
        backgroundColor: "#28a745",
        color: "white",
        border: "none",
        borderRadius: "3px",
        width: "100%",
    },
    undoButton: {
        marginTop: "5px",
        padding: "5px",
        fontSize: "12px",
        backgroundColor: "#ff9800",
        color: "white",
        border: "none",
        borderRadius: "3px",
        width: "100%",
    },
    frozenText: {
        fontSize: "12px",
        color: "green",
        fontWeight: "bold",
    },
};

export default CricketPrediction;
