import React, { useState } from "react";

const BALLS_PER_OVER = 6;

const CricketPrediction = () => {
    const initialOvers = Array.from({ length: 20 }, () => ({
        balls: [],
        isFrozen: false,
    }));

    const [overs, setOvers] = useState(initialOvers);

    const runOptions = ["0", "1", "2", "3", "4", "5", "6", "Wide", "No Ball", "Wicket"];

    const handlePrediction = (overIndex, run) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };
    
            if (currentOver.isFrozen) return newOvers;
    
            const balls = [...currentOver.balls];
            const legalBalls = balls.filter((ball) => ball !== "Wide" && ball !== "No Ball").length;
    
            if (legalBalls >= BALLS_PER_OVER) return newOvers;
    
            balls.push(run);
            currentOver.balls = balls;
            newOvers[overIndex] = currentOver;
    
            return newOvers;
        });
    };

    const deleteLastEntry = (overIndex) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            if (currentOver.isFrozen) return newOvers;

            const balls = [...currentOver.balls];
            if (balls.length > 0) balls.pop();

            currentOver.balls = balls;
            newOvers[overIndex] = currentOver;

            return newOvers;
        });
    };

    const playOver = (overIndex) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            const legalBalls = currentOver.balls.filter((ball) => ball !== "Wide" && ball !== "No Ball").length;
            if (legalBalls < BALLS_PER_OVER) {
                alert("You must enter all 6 legitimate balls before confirming!");
                return newOvers;
            }

            currentOver.isFrozen = true;
            newOvers[overIndex] = currentOver;
            return newOvers;
        });
    };

    const getBallStyle = (ball) => {
        if (ball === "Wicket") return { backgroundColor: "red", color: "white" };
        if (ball === "No Ball") return { backgroundColor: "blue", color: "white" };
        if (ball === "Wide") return { backgroundColor: "green", color: "white" };
        if (ball === "4" || ball === "6") return { color: "orange" };
        return { color: "black" };
    };

    return (
        <div style={styles.container}>
            <h1>Cricket Prediction</h1>

            <div style={styles.grid}>
                {overs.map((over, index) => {
                    return (
                        <div key={index} style={{ ...styles.overBox, backgroundColor: over.isFrozen ? "#e6f7e6" : "#f9f9f9" }}>
                            <h3 style={styles.overTitle}>Over {index + 1}</h3>

                            <div style={styles.ballDisplay}>
                                {over.balls.map((ball, i) => (
                                    <div key={i} style={styles.ballContainer}>
                                        <span style={styles.ballOrder}>{i + 1}</span>
                                        <span style={{ ...styles.ballCircle, ...getBallStyle(ball) }}>
                                            {ball === "Wicket" ? "WK" : ball}
                                        </span>
                                    </div>
                                ))}
                            </div>

                            <hr style={styles.separator} />

                            <div style={styles.buttonsContainer}>
                                {runOptions.map((run, i) => (
                                    <button
                                        key={i}
                                        onClick={() => handlePrediction(index, run)}
                                        style={{
                                            ...styles.runButton,
                                            backgroundColor: run === "Wicket" ? "#ff4d4d" : run === "Wide" || run === "No Ball" ? "#66cc66" : "#ffffff",
                                            color: run === "Wicket" ? "white" : "black",
                                        }}
                                        disabled={over.isFrozen}
                                    >
                                        {run}
                                    </button>
                                ))}
                            </div>

                            {!over.isFrozen ? (
                                <>
                                    <button onClick={() => deleteLastEntry(index)} style={styles.deleteButton}>
                                        Delete Last Entry
                                    </button>
                                    <button onClick={() => playOver(index)} style={styles.freezeButton}>
                                        Play
                                    </button>
                                </>
                            ) : (
                                <p style={styles.frozenText}>Over Confirmed ✅</p>
                            )}
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

const styles = {
    container: { padding: "10px", textAlign: "center", fontFamily: "Arial, sans-serif" },
    grid: { display: "grid", gridTemplateColumns: "repeat(5, 1fr)", gap: "10px", justifyContent: "center", padding: "10px" },
    overBox: { border: "1px solid #ddd", padding: "15px", borderRadius: "8px", boxShadow: "2px 2px 10px rgba(0,0,0,0.1)" },
    overTitle: { fontSize: "18px", fontWeight: "bold", color: "#333" },
    ballDisplay: { display: "flex", justifyContent: "center", gap: "12px", marginBottom: "10px", flexWrap: "wrap" },
    ballContainer: { display: "flex", flexDirection: "column", alignItems: "center" },
    ballOrder: { fontSize: "12px", fontWeight: "bold", color: "red" },
    ballCircle: { 
        width: "35px", 
        height: "35px", 
        display: "flex", 
        alignItems: "center", 
        justifyContent: "center", 
        borderRadius: "50%", 
        fontWeight: "bold", 
        border: "1px solid black", 
        fontSize: "14px", 
        boxShadow: "1px 1px 5px rgba(0,0,0,0.3)" 
    },
    separator: { margin: "10px 0", borderTop: "2px solid #ccc" },
    buttonsContainer: { display: "grid", gridTemplateColumns: "repeat(5, 1fr)", gap: "5px" },
    runButton: { padding: "8px", borderRadius: "5px", border: "1px solid #aaa", cursor: "pointer", fontSize: "14px" },
    deleteButton: { padding: "8px", marginTop: "5px", backgroundColor: "#ff4d4d", color: "white", border: "none", cursor: "pointer" },
    freezeButton: { padding: "8px", marginTop: "5px", backgroundColor: "#4CAF50", color: "white", border: "none", cursor: "pointer" },
    frozenText: { color: "green", fontWeight: "bold", marginTop: "5px" }
};

export default CricketPrediction;
