import React, { useState } from "react";

const BALLS_PER_OVER = 6;

const CricketPrediction = () => {
    const initialOvers = Array.from({ length: 20 }, () => ({
        balls: [],
        isFrozen: false,
    }));

    const [innings, setInnings] = useState(1);
    const [overs, setOvers] = useState(initialOvers);

    const runOptions = ["0", "1", "2", "3", "4", "5", "6", "Wide", "No Ball", "Wicket"];

    const handlePrediction = (overIndex, run) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            if (currentOver.isFrozen) return newOvers; // Stop updates if over is frozen

            const balls = [...currentOver.balls];
            const legalBalls = balls.filter((ball) => ball !== "Wide" && ball !== "No Ball").length;

            if (legalBalls >= BALLS_PER_OVER && run !== "Wide" && run !== "No Ball") return newOvers;

            balls.push(run);
            currentOver.balls = balls;
            newOvers[overIndex] = currentOver;

            return newOvers;
        });
    };

    const deleteLastEntry = (overIndex) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            if (currentOver.isFrozen) return newOvers; // Prevent deletion if frozen

            const balls = [...currentOver.balls];
            if (balls.length > 0) balls.pop();

            currentOver.balls = balls;
            newOvers[overIndex] = currentOver;

            return newOvers;
        });
    };

    const playOver = (overIndex) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            const legalBalls = currentOver.balls.filter((ball) => ball !== "Wide" && ball !== "No Ball").length;
            if (legalBalls < BALLS_PER_OVER) {
                alert("You must enter all 6 legitimate balls before confirming!");
                return newOvers;
            }

            currentOver.isFrozen = true;
            newOvers[overIndex] = currentOver;
            return newOvers;
        });
    };

    const undoOver = (overIndex) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            newOvers[overIndex] = { ...newOvers[overIndex], isFrozen: false };
            return newOvers;
        });
    };

    return (
        <div style={styles.container}>
            <h1>Cricket Prediction</h1>

            <div style={styles.grid}>
                {overs.map((over, index) => (
                    <div key={index} style={{ ...styles.overBox, backgroundColor: over.isFrozen ? "#d4edda" : "#f9f9f9" }}>
                        <h4>Over {index + 1}</h4>
                        <p style={styles.predictions}>{over.balls.length ? over.balls.join(" | ") : "No predictions yet"}</p>

                        <div style={styles.buttonsContainer}>
                            {runOptions.map((run, i) => (
                                <button
                                    key={i}
                                    onClick={() => handlePrediction(index, run)}
                                    style={{
                                        ...styles.runButton,
                                        fontSize: isNaN(run) ? "10px" : "12px",
                                        opacity: over.isFrozen ? 0.5 : 1,
                                        cursor: over.isFrozen ? "not-allowed" : "pointer",
                                    }}
                                    disabled={over.isFrozen}
                                >
                                    {run}
                                </button>
                            ))}
                        </div>

                        {!over.isFrozen ? (
                            <>
                                <button onClick={() => deleteLastEntry(index)} style={styles.deleteButton}>
                                    Delete Last Entry
                                </button>
                                <button onClick={() => playOver(index)} style={styles.freezeButton}>
                                    Play
                                </button>
                            </>
                        ) : (
                            <>
                                <p style={styles.frozenText}>Over Confirmed ✅</p>
                                <button onClick={() => undoOver(index)} style={styles.undoButton}>
                                    Undo
                                </button>
                            </>
                        )}
                    </div>
                ))}
            </div>

            <button onClick={() => setInnings((prev) => (prev === 1 ? 2 : 1))} style={styles.switchButton}>
                {innings === 1 ? "Switch to 2nd Innings" : "Switch to 1st Innings"}
            </button>
        </div>
    );
};

const styles = {
    container: {
        padding: "10px",
        textAlign: "center",
        fontFamily: "Arial, sans-serif",
    },
    grid: {
        display: "grid",
        gridTemplateColumns: "repeat(auto-fit, minmax(150px, 1fr))",
        gap: "10px",
        justifyContent: "center",
        padding: "10px",
    },
    overBox: {
        border: "1px solid #ccc",
        padding: "8px",
        borderRadius: "5px",
        textAlign: "center",
    },
    predictions: {
        fontSize: "12px",
        marginBottom: "5px",
        color: "#333",
    },
    buttonsContainer: {
        display: "flex",
        flexWrap: "wrap",
        justifyContent: "center",
        gap: "5px",
    },
    runButton: {
        padding: "8px",
        fontSize: "12px",
        backgroundColor: "#007bff",
        color: "white",
        border: "none",
        borderRadius: "3px",
        minWidth: "40px",
    },
    deleteButton: {
        marginTop: "5px",
        padding: "5px",
        fontSize: "12px",
        backgroundColor: "#dc3545",
        color: "white",
        border: "none",
        borderRadius: "3px",
        width: "100%",
    },
    freezeButton: {
        marginTop: "5px",
        padding: "5px",
        fontSize: "12px",
        backgroundColor: "#28a745",
        color: "white",
        border: "none",
        borderRadius: "3px",
        width: "100%",
    },
    undoButton: {
        marginTop: "5px",
        padding: "5px",
        fontSize: "12px",
        backgroundColor: "#ff9800",
        color: "white",
        border: "none",
        borderRadius: "3px",
        width: "100%",
    },
    frozenText: {
        fontSize: "12px",
        color: "green",
        fontWeight: "bold",
    },
    switchButton: {
        marginTop: "10px",
        padding: "10px",
        backgroundColor: "#17a2b8",
        color: "white",
        border: "none",
        borderRadius: "5px",
        fontSize: "14px",
        cursor: "pointer",
    },
};

export default CricketPrediction;
