import React, { useState } from "react";

const BALLS_PER_OVER = 6; // Number of legitimate balls per over
const MAX_WICKETS = 10; // Maximum wickets in an innings

const CricketPrediction = () => {
    // Initialize overs with empty predictions and locked status
    const initialOvers = Array.from({ length: 20 }, () => ({
        balls: [], // Predictions for each ball
        locked: false, // If predictions are frozen
    }));

    const [innings, setInnings] = useState(1); // Current innings (1 or 2)
    const [overs, setOvers] = useState(initialOvers); // Predictions
    const [wickets, setWickets] = useState(0); // Total wickets fallen
    const [runs, setRuns] = useState(0); // Total runs scored
    const [actualEvents, setActualEvents] = useState(Array(20).fill([])); // Actual match events

    const runOptions = [0, 1, 2, 3, 4, 5, 6, "Wide", "No Ball", "Wicket"]; // Ball outcomes

    // Handle predictions
    const handlePrediction = (overIndex, run) => {
        if (overs[overIndex].locked || wickets >= MAX_WICKETS) return; // Skip if over is locked

        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };
            const balls = [...currentOver.balls];

            // Count legal deliveries
            const legalBalls = balls.filter((ball) => ball !== "Wide" && ball !== "No Ball").length;

            if (legalBalls >= BALLS_PER_OVER && run !== "Wide" && run !== "No Ball") {
                return newOvers; // Don't exceed 6 legal balls
            }

            balls.push(run);
            currentOver.balls = balls;
            newOvers[overIndex] = currentOver;

            // Update runs
            if (typeof run === "number") {
                setRuns((prevRuns) => prevRuns + run);
            } else if (run === "Wide" || run === "No Ball") {
                setRuns((prevRuns) => prevRuns + 1); // Extra run
            }

            return newOvers;
        });

        // **Fix: Ensure each "Wicket" click adds only 1 wicket**
        if (run === "Wicket") {
            setWickets((prevWickets) => Math.min(MAX_WICKETS, prevWickets + 1)); // Prevent exceeding MAX_WICKETS
        }
    };

    // Copy previous over's predictions
    const copyOver = (overIndex) => {
        if (overIndex === 0 || overs[overIndex - 1].balls.length === 0) return;

        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            newOvers[overIndex].balls = [...prevOvers[overIndex - 1].balls];
            return newOvers;
        });
    };

    // Delete the last prediction
    const deleteLastEntry = (overIndex) => {
        if (overs[overIndex].locked) return;

        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            if (currentOver.balls.length > 0) {
                const lastBall = currentOver.balls.pop();

                // Update runs
                if (typeof lastBall === "number") {
                    setRuns((prevRuns) => Math.max(0, prevRuns - lastBall));
                } else if (lastBall === "Wide" || lastBall === "No Ball") {
                    setRuns((prevRuns) => Math.max(0, prevRuns - 1));
                }

                // Update wickets
                if (lastBall === "Wicket") {
                    setWickets((prevWickets) => Math.max(0, prevWickets - 1));
                }
            }

            newOvers[overIndex] = currentOver;
            return newOvers;
        });
    };

    // Lock predictions for an over
    const freezePredictions = (overIndex) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            newOvers[overIndex].locked = true;
            return newOvers;
        });
    };

    // Update actual match events
    const updateActualEvents = (overIndex, actualBalls) => {
        setActualEvents((prevEvents) => {
            const newEvents = [...prevEvents];
            newEvents[overIndex] = actualBalls;
            return newEvents;
        });
    };

    // Calculate accuracy percentage
    const calculateMatchPercentage = (overIndex) => {
        if (actualEvents[overIndex].length === 0 || overs[overIndex].balls.length === 0) return "N/A";

        const predicted = overs[overIndex].balls;
        const actual = actualEvents[overIndex];

        let correctCount = 0;
        predicted.forEach((pred, i) => {
            if (actual[i] !== undefined && pred === actual[i]) correctCount++;
        });

        return ((correctCount / BALLS_PER_OVER) * 100).toFixed(2) + "%";
    };

    return (
        <div style={{ padding: "20px" }}>
            <h1>Cricket Prediction Game - Innings {innings}</h1>
            <h2>Runs: {runs} | Wickets: {wickets}/{MAX_WICKETS}</h2>

            {overs.map((over, index) => {
                const legalBalls = over.balls.filter((ball) => ball !== "Wide" && ball !== "No Ball").length;
                const overLocked = over.locked || legalBalls >= BALLS_PER_OVER;

                return (
                    <div key={index} style={{ marginBottom: "20px", border: "1px solid #ccc", padding: "10px", borderRadius: "5px" }}>
                        <h3>Over {index + 1}</h3>
                        <div>
                            {runOptions.map((run, i) => (
                                <button
                                    key={i}
                                    onClick={() => handlePrediction(index, run)}
                                    disabled={overLocked || wickets >= MAX_WICKETS}
                                    style={{ margin: "5px", padding: "5px 10px" }}
                                >
                                    {run}
                                </button>
                            ))}
                        </div>
                        <p>Match %: {calculateMatchPercentage(index)}</p>
                    </div>
                );
            })}

            <button onClick={() => setInnings((prev) => (prev === 1 ? 2 : 1))} style={{ marginTop: "20px", padding: "10px 20px" }}>
                {innings === 1 ? "Switch to 2nd Innings" : "Switch to 1st Innings"}
            </button>
        </div>
    );
};

export default CricketPrediction;
