import React, { useState } from "react";

const BALLS_PER_OVER = 6;

const CricketPrediction = () => {
    const initialOvers = Array.from({ length: 20 }, () => ({
        balls: [],
        isFrozen: false,
    }));

    const [overs, setOvers] = useState(initialOvers);
    const runOptions = ["0", "1", "2", "3", "4", "5", "6", "Wide", "No Ball", "Wicket"];

    const handlePrediction = (overIndex, run) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            if (currentOver.isFrozen) return newOvers;

            const balls = [...currentOver.balls];
            const legalBalls = balls.filter((ball) => ball !== "Wide" && ball !== "No Ball").length;

            if (legalBalls >= BALLS_PER_OVER) return newOvers;

            balls.push(run);
            currentOver.balls = balls;
            newOvers[overIndex] = currentOver;

            return newOvers;
        });
    };

    const deleteLastEntry = (overIndex) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            if (currentOver.isFrozen) return newOvers;

            const balls = [...currentOver.balls];
            if (balls.length > 0) balls.pop();

            currentOver.balls = balls;
            newOvers[overIndex] = currentOver;

            return newOvers;
        });
    };

    const playOver = (overIndex) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            const legalBalls = currentOver.balls.filter((ball) => ball !== "Wide" && ball !== "No Ball").length;
            if (legalBalls < BALLS_PER_OVER) {
                alert("You must enter all 6 legitimate balls before confirming!");
                return newOvers;
            }

            currentOver.isFrozen = true;
            newOvers[overIndex] = currentOver;
            return newOvers;
        });
    };

    const copyToNextOver = (overIndex) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];

            if (overIndex >= 19) return newOvers;

            const currentOver = { ...newOvers[overIndex] };
            const nextOver = { ...newOvers[overIndex + 1] };

            const legalBalls = currentOver.balls.filter((ball) => ball !== "Wide" && ball !== "No Ball").length;

            if (legalBalls < BALLS_PER_OVER || nextOver.balls.length > 0) return newOvers;

            nextOver.balls = [...currentOver.balls];
            newOvers[overIndex + 1] = nextOver;

            return newOvers;
        });
    };

    return (
        <div style={styles.container}>
            <h2>Cricket Score Predictor</h2>

            {overs.map((over, index) => {
                const isCopyEnabled = over.balls.filter((ball) => ball !== "Wide" && ball !== "No Ball").length === BALLS_PER_OVER && index < 19 && overs[index + 1].balls.length === 0;

                return (
                    <div key={index} style={styles.overBox}>
                        <h4>Over {index + 1}</h4>

                        <div style={styles.ballDisplay}>
                            {over.balls.map((ball, i) => (
                                <div key={i} style={styles.ballCircle}>
                                    {ball}
                                </div>
                            ))}
                        </div>

                        <div style={styles.buttonGroup}>
                            {runOptions.map((run, i) => (
                                <button
                                    key={i}
                                    onClick={() => handlePrediction(index, run)}
                                    style={styles.runButton}
                                    disabled={over.isFrozen}
                                >
                                    {run}
                                </button>
                            ))}
                        </div>

                        <div style={styles.actionButtons}>
                            {!over.isFrozen ? (
                                <>
                                    <button onClick={() => deleteLastEntry(index)} style={styles.deleteButton}>Delete Last</button>
                                    <button onClick={() => playOver(index)} style={styles.playButton}>Play</button>
                                    <button
                                        onClick={() => copyToNextOver(index)}
                                        style={{ ...styles.copyButton, opacity: isCopyEnabled ? 1 : 0.5, cursor: isCopyEnabled ? "pointer" : "not-allowed" }}
                                        disabled={!isCopyEnabled}
                                    >
                                        Copy to Next Over
                                    </button>
                                </>
                            ) : (
                                <p style={styles.frozenText}>Over Confirmed âœ…</p>
                            )}
                        </div>
                    </div>
                );
            })}
        </div>
    );
};

const styles = {
    container: {
        textAlign: "center",
        fontFamily: "Arial, sans-serif",
        padding: "10px",
    },
    overBox: {
        border: "2px solid #ccc",
        padding: "15px",
        borderRadius: "8px",
        margin: "10px auto",
        backgroundColor: "#f9f9f9",
        maxWidth: "400px",
    },
    ballDisplay: {
        display: "grid",
        gridTemplateColumns: "repeat(6, 1fr)",
        gap: "8px",
        justifyContent: "center",
        marginBottom: "10px",
    },
    ballCircle: {
        width: "30px",
        height: "30px",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        borderRadius: "50%",
        fontWeight: "bold",
        border: "1px solid black",
        backgroundColor: "#fff",
    },
    buttonGroup: {
        display: "grid",
        gridTemplateColumns: "repeat(5, 1fr)",
        gap: "8px",
        justifyContent: "center",
    },
    runButton: {
        padding: "8px",
        borderRadius: "5px",
        border: "1px solid #ccc",
        backgroundColor: "#eee",
        cursor: "pointer",
        fontSize: "14px",
    },
    actionButtons: {
        display: "flex",
        justifyContent: "center",
        gap: "10px",
        marginTop: "10px",
    },
    deleteButton: {
        padding: "8px",
        backgroundColor: "#ff6b6b",
        color: "white",
        borderRadius: "5px",
        border: "none",
        cursor: "pointer",
    },
    playButton: {
        padding: "8px",
        backgroundColor: "#4CAF50",
        color: "white",
        borderRadius: "5px",
        border: "none",
        cursor: "pointer",
    },
    copyButton: {
        padding: "8px",
        backgroundColor: "#2196F3",
        color: "white",
        borderRadius: "5px",
        border: "none",
        cursor: "pointer",
    },
    frozenText: {
        fontSize: "14px",
        fontWeight: "bold",
        color: "green",
    },
};

export default CricketPrediction;
