import React, { useState } from "react";

const BALLS_PER_OVER = 6;

const CricketPrediction = () => {
    const initialOvers = Array.from({ length: 20 }, () => ({
        balls: [],
        isFrozen: false,
    }));

    const [overs, setOvers] = useState(initialOvers);

    const runOptions = ["0", "1", "2", "3", "4", "5", "6"];
    const extraOptions = ["Wide", "No Ball", "Wicket"];

    const handlePrediction = (overIndex, run) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            if (currentOver.isFrozen) return newOvers;

            const balls = [...currentOver.balls];
            const legalBalls = balls.filter((ball) => ball !== "Wide" && ball !== "No Ball").length;

            if (legalBalls >= BALLS_PER_OVER && run !== "Wide" && run !== "No Ball") return newOvers;

            balls.push(run);
            currentOver.balls = balls;
            newOvers[overIndex] = currentOver;

            return newOvers;
        });
    };

    const deleteLastEntry = (overIndex) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            if (currentOver.isFrozen) return newOvers;

            const balls = [...currentOver.balls];
            if (balls.length > 0) balls.pop();

            currentOver.balls = balls;
            newOvers[overIndex] = currentOver;

            return newOvers;
        });
    };

    const playOver = (overIndex) => {
        setOvers((prevOvers) => {
            const newOvers = [...prevOvers];
            const currentOver = { ...newOvers[overIndex] };

            const legalBalls = currentOver.balls.filter((ball) => ball !== "Wide" && ball !== "No Ball").length;
            if (legalBalls < BALLS_PER_OVER) {
                alert("You must enter all 6 legitimate balls before confirming!");
                return newOvers;
            }

            currentOver.isFrozen = true;
            newOvers[overIndex] = currentOver;
            return newOvers;
        });
    };

    const getOrdinal = (num) => {
        const suffixes = ["th", "st", "nd", "rd"];
        const v = num % 100;
        return num + (suffixes[(v - 20) % 10] || suffixes[v] || suffixes[0]);
    };

    return (
        <div style={styles.container}>
            <h1>Cricket Prediction</h1>

            <div style={styles.grid}>
                {overs.map((over, index) => {
                    return (
                        <div key={index} style={{ ...styles.overBox, backgroundColor: over.isFrozen ? "#d4edda" : "#f9f9f9" }}>
                            <h4>Over {index + 1}</h4>

                            <div style={styles.ballDisplay}>
                                {over.balls.map((ball, i) => (
                                    <div key={i} style={styles.ballContainer}>
                                        <span style={styles.ballOrder}>{getOrdinal(i + 1)}</span>
                                        <span style={{ ...styles.ballCircle, ...getBallStyle(ball) }}>
                                            {ball === "Wicket" ? "WK" : ball === "No Ball" ? "NB" : ball === "Wide" ? "WD" : ball}
                                        </span>
                                    </div>
                                ))}
                            </div>

                            <div style={styles.buttonsContainer}>
                                {runOptions.map((run, i) => (
                                    <button
                                        key={i}
                                        onClick={() => handlePrediction(index, run)}
                                        style={styles.runButton}
                                        disabled={over.isFrozen}
                                    >
                                        {run}
                                    </button>
                                ))}
                            </div>

                            <div style={styles.extrasContainer}>
                                {extraOptions.map((run, i) => (
                                    <button
                                        key={i}
                                        onClick={() => handlePrediction(index, run)}
                                        style={styles.extraButton}
                                        disabled={over.isFrozen}
                                    >
                                        {run}
                                    </button>
                                ))}
                            </div>

                            {!over.isFrozen ? (
                                <>
                                    <button onClick={() => deleteLastEntry(index)} style={styles.deleteButton}>
                                        Delete Last Entry
                                    </button>
                                    <button onClick={() => playOver(index)} style={styles.freezeButton}>
                                        Play
                                    </button>
                                </>
                            ) : (
                                <p style={styles.frozenText}>Over Confirmed âœ…</p>
                            )}
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

const getBallStyle = (ball) => ({
    backgroundColor: ball === "Wicket" ? "red" :
                     ball === "No Ball" ? "blue" :
                     ball === "Wide" ? "green" :
                     ball === "4" || ball === "6" ? "orange" : "white",
    color: ball === "4" || ball === "6" ? "black" : "white",
});

const styles = {
    container: { padding: "10px", textAlign: "center", fontFamily: "Arial, sans-serif" },
    grid: { display: "grid", gridTemplateColumns: "repeat(5, 1fr)", gap: "10px", padding: "10px" },
    overBox: { padding: "10px", borderRadius: "10px", textAlign: "center", border: "1px solid #ddd" },
    ballDisplay: { display: "grid", gridTemplateColumns: "repeat(6, 1fr)", gap: "10px", justifyContent: "center", marginTop: "10px" },
    ballContainer: { display: "flex", flexDirection: "column", alignItems: "center" },
    ballOrder: { fontSize: "12px", fontWeight: "bold", marginBottom: "3px" },
    ballCircle: {
        width: "40px",
        height: "40px",
        borderRadius: "50%",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        fontSize: "18px",
        fontWeight: "bold",
        border: "2px solid #000",
    },
    buttonsContainer: { display: "flex", flexWrap: "wrap", justifyContent: "center", gap: "5px", marginTop: "10px" },
    extrasContainer: { display: "flex", justifyContent: "center", gap: "5px", marginTop: "5px" },
    deleteButton: { marginTop: "10px", padding: "5px", backgroundColor: "#ff4444", color: "white", border: "none", borderRadius: "5px" },
    freezeButton: { marginTop: "5px", padding: "5px", backgroundColor: "#008CBA", color: "white", border: "none", borderRadius: "5px" },
    frozenText: { fontWeight: "bold", marginTop: "5px", color: "green" },
};

export default CricketPrediction;
